package com.duan.day03;

public class demo07 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//需求：打印100遍HelloJava
	     //1、初始部分
	     int i = 1;
	     do{
	         //3、循环操作
	         System.out.println(i+"HelloJava");
	         //4、迭代部分
	         i++;
	     }while(i <= 100);
	     System.out.println(i);
	     System.out.println("程序结束");
	}

}
