package com.duan.day1;

public class demo2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//先声明，再赋值
		//数据类型 变量名;变量名 = 值;
		int age;
		age = 18;
		System.out.println(age);
		//声明并赋值
		//数据类型 变量名 = 值;
		int length = 18;
		System.out.println(length);
		//多个同类型变量的声明与赋值
		int a,b,c;
        a = 5;
        b = 6;
        c = 7;
        System.out.println(a);
        System.out.println(b);
        System.out.println(c);
	}

}
